    this.body.nodes[clusterNodeProperties.id] = clusterNode;

    this._clusterEdges(
      childNodesObj,
      childEdgesObj,
      clusterNodeProperties,
      options.clusterEdgeProperties,
    );

    // set ID to undefined so no duplicates arise
    clusterNodeProperties.id = undefined;

